import React, { useState, useRef, useEffect } from 'react';
import '../components/Navigation.css';
import logo from '../media/images/myriad_logo.png';

const Navigation = () => {
    const menuRef = useRef(null);
    const [slide, setSlide] = useState(false);
    const slideMenuDown = () => setSlide(!slide);

    useEffect(() => {
        let menuButton = document.getElementById("menuBtn");

        if(slide !== false) {
            menuButton.innerText = "Close";
        } else {
            menuButton.innerText = "Menu";
        }

    }, [slide]);

    return (
        <div className="nav__container">
            <div className="nav__bar">
                <a className="nav__title" href="/">
                    <img src={logo} alt="logo"/>
                    <span className={`title ${slide ? "active" : "inactive"}`}>Myriad</span>
                </a>

                <button id="menuBtn" className={`nav__button ${slide ? "active" : "inactive"}`} onClick={slideMenuDown}>Menu</button>
            </div>

            <div ref={menuRef} className={`menu ${slide ? "active" : "inactive"}`}>
                <ul className={`menu__items ${slide ? "active" : "inactive"}`}>
                    <li className="menu__links" onClick={slideMenuDown}>Why Myriad</li>
                    <li className="menu__links" onClick={slideMenuDown}>Community</li>
                    <li className="menu__links" onClick={slideMenuDown}>Merchants</li>
                    <li className="menu__links" onClick={slideMenuDown}>Wallets</li>
                    <li className="menu__links" onClick={slideMenuDown}>Purchase</li>
                    <li className="menu__links" onClick={slideMenuDown}>Mining</li>
                    <li className="menu__links" onClick={slideMenuDown}>Resources</li>
                </ul>
            </div>
        </div>
    )
}

export default Navigation
