import React, { useState, useRef } from 'react';
import '../components/Navigation.css';
import logo from '../media/images/myriad_logo.png';

const Navigation = () => {
    const menuRef = useRef(null);
    const [slide, setSlide] = useState(false);
    const slideMenuDown = () => setSlide(!slide);

    return (
        <div className="nav__container">
            <div className="nav__bar">
                <a className="nav__title" href="/">
                    <img src={logo} alt="logo"/>
                    <span className={`title ${slide ? "active" : "inactive"}`}>Myriad</span>
                </a>

                <button className={`nav__button ${slide ? "active" : "inactive"}`} onClick={slideMenuDown}>Menu</button>
            </div>

            <div ref={menuRef} className={`menu ${slide ? "active" : "inactive"}`}>
                <ul>
                    <li>Why Myriad</li>
                    <li>Community</li>
                    <li>Merchants</li>
                    <li>Wallets</li>
                    <li>Purchase</li>
                    <li>Mining</li>
                    <li>Resources</li>
                </ul>
            </div>
        </div>
    )
}

export default Navigation
